<?php

/**
 * Formulario5Table
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class Formulario5Table extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object Formulario5Table
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('Formulario5');
    }
    public function selectForms5DeMedicamento()
    {
        $user = sfContext::getInstance()->getUser();
        $producto = $user->getAttribute('producto');
        $q = Doctrine_Query::create()
                    ->from('Formulario5 f')
                    ->leftJoin('f.Producto p')
                    ->leftJoin('f.TipoTramiteFormulario5 tf')
                    ->leftJoin('f.TipoProductoFormulario5 tp')
                    ->leftJoin('f.OrigenFormulario5 of')
                    ->where('f.producto_id = ?', $producto->getId())
                    ->orderBy('f.id ASC');
            
        return $q;
    }
    
    public function selectForms5DeEmpresa()
    {
        $user = sfContext::getInstance()->getUser();
        $empresa = $user->getAttribute('empresa');
        $q = Doctrine_Query::create()
                    ->from('Formulario5 f')
                    ->leftJoin('f.Producto p')
                    ->leftJoin('p.Empresa e ON id = ?', $empresa->getId())

//                    ->leftJoin('p.TipoTramiteFormulario5 tf')
//                    ->leftJoin('p.TipoProductoFormulario5 tp')
//                    ->leftJoin('p.OrigenFormulario5 of')
                    
                    ->orderBy('f.id ASC');
        return $q;
    }
}